using Microsoft.Extensions.Options;
using System;
using System.Collections.Generic;
using System.Net.Http;
using System.Text;
using System.Text.Json;
using System.Threading.Tasks;



namespace FBMICService.Utility
{
    public class EmailSender : IEmailSender
    {
        private readonly EmailOptions emailOptions;

        public EmailSender(IOptions<EmailOptions> options)
        {
            emailOptions = options.Value;
        }

        public async Task<string> SendEmailNotification(string toMail, string ccMail, string subject, string body, string bcc = null)
        {
            var client = new HttpClient();
            var jsonData = JsonSerializer.Serialize(new
            {
                toemail = toMail,
                ccemail = ccMail,
                task = subject,
                body = body,
                bccemail = bcc
                //,
                //attachment = ""
            });

            HttpResponseMessage result = await client.PostAsync(emailOptions.LogicAppSendEmailUrl,
                new StringContent(jsonData, Encoding.UTF8, "application/json"));

            var statusCode = result.StatusCode.ToString();
            return statusCode;
        }

        
    }
}
